mapper.insert:
INSERT INTO BOARD
	( b_title
	  , b_writer
	  , b_content
	  , b_grpno
	  , b_category
	  , b_grpord
	  , b_depth
	   )
VALUES ( #{b_title}
	  , #{b_writer}
	  , #{b_content}
	  , #{b_grpno}
	  , #{b_category}
	  , 0
	  , 0
	   )

mapper.update:
UPDATE BOARD
SET b_title=#{b_title}
	, b_content=#{b_content}
	  WHERE b_no=#{b_no}

mapper.select:
SELECT *
FROM USER
WHERE user_id = #{user_id}
AND user_pwd = #{user_pwd}
