mapper.insert:
INSERT INTO BOARD
	( b_no
	  ,	b_title
	  , b_writer
	  , b_content
	  , b_refno
	  , b_category
	  , b_depth
	  , b_readcnt
	  , b_order
	   )
VALUES ( #{b_no}
	  ,	#{b_title}
	  , #{b_writer}
	  , #{b_content}
	  , #{b_refno}
	  , #{b_category}
	  , 1
	  , 0
	  , #{b_order}
	   )

mapper.update:
UPDATE BOARD
SET b_title=#{b_title}
	, b_content=#{b_content}
WHERE b_no=#{b_no} AND b_writer = #{b_writer}

mapper.delete:

	DELETE FROM BOARD
	WHERE b_no = #{b_no} OR b_refno = #{b_no}


	DELETE FROM BOARD
	WHERE b_no = #{b_no}


AND b_writer = #{b_writer}

mapper.update:

UPDATE board 
SET b_readcnt = b_readcnt + 1
WHERE b_no = #{b_no}

mapper.select:
